  and teacher = #{teacher,jdbcType=VARCHAR}
  and lesson = #{lesson,jdbcType=VARCHAR}
  and years = #{years,jdbcType=INTEGER}
  and teach_class = #{teachClass,jdbcType=INTEGER}

